const rangePercentage = (input, range_min, range_max, range_2ndMax) => {
	let percentage = ((input - range_min) * 100) / (range_max - range_min);

	if (percentage > 100) {
		if (range_2ndMax !== undefined) {
			percentage = ((range_2ndMax - input) * 100) / (range_2ndMax - range_max);
			if (percentage < 0) {
				percentage = 0;
			}

		} else {
			percentage = 100;
		}
	} else if (percentage < 0) {
		percentage = 0;
	}

	const percent = (percentage / 100)
	return percent;
}

const percentageToHsl = (percentage, hue0, hue1) => {
	const hue = (percentage * (hue1 - hue0)) + hue0;
	return 'hsl(' + hue + ', 100%, 50%)';
}

const celsiusToColor = (celsius) => {
	const percentage = rangePercentage(celsius, -20, 40);
	const celsiusColor = percentageToHsl(percentage, 0.55, 0);
	return celsiusColor;
}

const run = () => {
	const heatmapString = ctrl.data[0].datapoints.slice(-1)[0][0].replace(/\\n/g, '')
	const heatmap = heatmapString.split(',')
	var svg = Snap(svgnode);
	for (var i = 0; i < 32; i++) {
		for (var j = 0; j < 24; j++) {
			const rect = svg.select(`#x${j}-y${i}`);
			rect.attr({ "fill": celsiusToColor(heatmap[i * 24 + j]) });
		}
	}
}

run();

panels.js_init_code:
const RECT_WIDTH = 16.5;
const RECT_HEIGHT = 16.5;
const RECT_SPACEING = 2;
const RECT_COLOR = "#000000";
const RECT_OPACITY = 0.5;

const svg = Snap(svgnode);

for (let y = 0; y < 32; y++) {
	for (let x = 0; x < 24; x++) {
		const rectX = x * (RECT_WIDTH + RECT_SPACEING);
		const rectY = y * (RECT_HEIGHT + RECT_SPACEING);
		const createdRect = svg.rect(rectX, rectY, RECT_WIDTH, RECT_HEIGHT);
		createdRect.attr({
			fill: RECT_COLOR,
			opacity: RECT_OPACITY,
			id: `x${x}-y${y}`
		});
	}
}

